state.extend = underscore.extend.apply;
state.args = null;
state.source = null;

on.input.in = function() {
  if(state.args) {
     state.args.unshift($.in);
     output({out: $.write('in', state.extend(null, state.args))});
     state.args = null;
  } else {
    state.args = [$.in];
  }
};

on.input.source = function() {

  if(state.args) {
     state.args.push($.source);
     output({out: $.write('in', state.extend(null, state.args))});
     state.args = null;
  } else {
    state.args = [$.source];
  }

};

on.input.in = function() {
  var i;
  var obj = $.object ? $.object : {};
  if($.keys.length !== $.in.length) {
    output({error: $.create(Error('Key length does not match value length'))});
  } else {
    for(i = 0; i < $.keys.length; i++) {
     obj[$.keys[i]] = $.in[i];
    }
    output({out: $.write('in', obj)});
  }
};

